function coeff = create_eqn2(eq,x2)
eq = permute(eq,[1,3,2]);
coeff = [eq(1),...
         eq(2)*x2+eq(12),...
         eq(3)*x2^2+eq(13)*x2+eq(22),...
         eq(4)*x2^3+eq(14)*x2^2+eq(23)*x2,...
         eq(5)*x2^4+eq(15)*x2^3+eq(24)*x2^2,...
         eq(6)*x2^5+eq(16)*x2^4+eq(25)*x2^3,...
         eq(7)*x2^6+eq(17)*x2^5+eq(26)*x2^4,...
         eq(8)*x2^7+eq(18)*x2^6+eq(27)*x2^5,...                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     ,...
         eq(9)*x2^8+eq(19)*x2^7+eq(28)*x2^6,...
         eq(10)*x2^9+eq(20)*x2^8+eq(29)*x2^7,...
         eq(11)*x2^10+eq(21)*x2^9+eq(30)*x2^8];